create table avion(
    idavion varchar(10) default 'AV' || nextval('avion_seq') primary key,
    idMarque int not null references marque(id),
    idPhoto int references photo_avion(id),
    numeromatricule varchar(20) not null,
    datedebut date default current_timestamp
