create
or replace view assurance_avion_detail as
select
    avion.idavion,
    avion.numeromatricule,
    avion.datedebut dateutilisation,
    assurance_avion.datedebut,
    assurance_avion.datefin,
    prix,
    assurance_avion.idtypeassurance,
    nomassurance
from
    avion
    join assurance_avion on avion.idavion = assurance_avion.idavion
    join typeassurance on typeassurance.idtypeassurance = assurance_avion.idtypeassurance;